import React from 'react';
import axios from 'axios';
import './App.css';
import ListItem from './ListItem';
import ListContainer from './ListContainer'
import { Link } from 'react-router-dom';
//API https://www.googleapis.com/books/v1/volumes?q=quilting.
export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      addIteme: [],
      booklist:[],

      currentItem:{
        text:'',
        key:''
      }
    }
    this.addItem = this.addItem.bind(this);
    this.handleInput = this.handleInput.bind(this);
    this.deleteItem = this.deleteItem.bind(this);
    this.setUpdate = this.setUpdate.bind(this);
  }
  addItem = (e) => {
    e.preventDefault();
    console.log('Add Item!');
    this.setState({
      addIteme: [...this.state.addIteme, this.state.newItem],
      newItem: ''
    });
  }
  onTextBoxChange = (e) => {

    this.setState({
      newItem: e.target.value
    });
  }
  componentDidMount() {
    console.log(this.state)
    axios({
      method: 'get',
      url: 'https://www.googleapis.com/books/v1/volumes?q=quilting',

    })
      .then(res => {
        console.log('RESPONSE: ', res);
        const answer = res.data.items;
        let titlesArr = [];
        for (let i=0;i<answer.length;i++){
          titlesArr.push(answer[i].volumeInfo.title)
        }
        this.setState({
          booklist: titlesArr
        })  
        console.log(this.state.booklist)
      })
      .catch(err => {
        console.log('ERROR: ', err);
      });
  };
  deleteallitem(key){
    const filteredItems= this.state.addIteme.filter(item =>
      item.key!==key);
    this.setState({
      addIteme: filteredItems
    })}  
    deleteitem(key){
      const filteredItems1= this.state.addIteme.filter(item =>
        item.key!==key);
      this.setState({
        addIteme: filteredItems1
      })}  
       editTask(task,i){
    this.state.taskName =task.title;
    this.state.data.splice(i,1);
    this.setState(this.state);
    }
    handleInput(e){
      this.setState({
        currentItem:{
          text: e.target.value,
          key: Date.now()
        }
      })}
  render() {
    return (
      <div>
        <form>
          <div className="Form">
            <label className="label">Add new Item</label>
            <input type="text" value={this.state.newItem} onChange={this.onTextBoxChange}></input>
            <button onClick={this.addItem} type="submit" className="button">Add</button>
            <button onClick={this. deleteallitem} type="submit" className="button"> deleteallitem</button>
            <ListContainer addIteme={this.state.addIteme}  deleteitem={this.deleteitem}   editTask={this.editTask}/>
          </div>
        </form>
        <h1>List of recommended books</h1>
    <ul><li>{this.state.booklist[0]}</li>
    <li>{this.state.booklist[1]}</li>
    <li>{this.state.booklist[2]}</li></ul>
      </div>

    )
  }
}
