import React from 'react';
import './ListItem.css';
export default class ListItem extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      setEditing:false
      }
    }
    toggleState=()=>{
      let {isEdit}=this.state;
      this.setState({
        isEdit:!isEdit
      })
    }
    updateForm=()=>{
      return(
        <form onClick={this.ubdatebookname}>
          <input type="text" ref={(v)=>{this.input=v}}></input>
          <button>Update </button>
        </form>
      )
    }
    ubdatebookname=(e)=>{

      this.props.edititem(this.props.index,this.input.value)
      this.toggleState();}
  render() {
    let {isEdit}=this.state;
    return (
      <div>
      <p className="list" >{this.props.taskName}</p>
      <button onClick={()=>this.props.deleteitem()} type="button" className="button"> deleteitem</button>
      <button onClick={()=> this.toggleState()}type="button" className='button-edit'>Edit </button > 
      <button onClick={()=> this.props.Markcomplete()} type="button" className="button">Markcomplete</button>
      <Fragment>
        {isEdit? this.updateForm():this.toggleState}
      </Fragment>
      </div>
    );
  }
}
