import React from 'react';
import axios from 'axios';
import Listitem from './ListItem';
import ListContainer from './ListContainer'
//API https://www.googleapis.com/books/v1/volumes?q=quilting.
export default class App extends React.Component{
  constructor(props){
    super(props);
  this.state={ 
    AddIteme:[{id:"Quilting For Dummies"}]
  }}
  addItem = (e) => {
    e.preventDefault();
    console.log('Add Item!');
    this.setState({
      AddIteme: [...this.state.AddIteme, this.state.newItem],
      newItem: ''
    });
  }
  onTextBoxChange = (e) => {

    this.setState({
      newItem: e.target.value
    });
  }
  deletItem=(e) =>{
   let Item=this.state.AddIteme;
   let i =Item.findIndex(Item=> Item.e===e)
   Item.splice(i,1)
   Item.setState({Item})
  }

render(){
  const {deletItem}=this.props; 
  return(
    <div>
      <form>
        <div className="Form">
          <label className="label">Add new Item</label>
          <input type="text" value={this.state.newItem} onChange={this.onTextBoxChange}></input>
          <button onClick={this.addItem} type="submit" className="button">Add</button>
          <button onClick={this.deletItem} type="submit" className="button">deletItem</button>
        </div>
      </form>  
      <ListContainer AddIteme={this.state.AddIteme} deletItem={this.deletItem}/>
</div>
  )}}
